<?php

namespace Gopro\Vipac\ProveedorBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * ArchivoRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CaracteristicatipoRepository extends EntityRepository{
    public function findAll()
    {
        return $this->findBy(array(), array('prioridad' => 'ASC'));
    }
}
